package prueba4;import java.util.Scanner;public class ejercicio1 {public static void main(String[] args) {Scanner sc = new Scanner(System.in);int masss = 0;System.out.println("Introduce un numero entero: ");int numero = sc.nextInt();int[] lista = new int[10];while (numero != 0 && masss < lista.length || masss == 10) {lista[masss] = numero;masss++;if (masss == 10) { break; } System.out.println("Introduce un numero entero (o 0 para salir): "); numero = sc.nextInt(); }int suma = 0;int resta = 0;int sumaNegativos = 0;int sumaPositivos = 0;for (int i = 0; i < masss; i++) {if (lista[i] > 0) { resta++; sumaPositivos += lista[i]; } else { suma++; sumaNegativos += lista[i]; } }if (suma == 0) { System.out.println("La media de los numeros negativos es: 0"); } else { double mediaNegativos = (double) (sumaNegativos / suma); System.out.println("La media de los numeros negativos es: " + mediaNegativos); }if (resta == 0) { System.out.println("La media de los numeros positivos es: 0"); } else { double mediaPositivos = (double) (sumaPositivos / resta); System.out.println("La media de los numeros positivos es: " + mediaPositivos); }sc.close();}}